<script>
    function displayFileName(input, targetSpan) {
        var fileInput = input;
        var fileName = fileInput.files[0].name;
        var span = document.getElementById(targetSpan);
        span.innerHTML = fileName;

        // Hiển thị hình ảnh nếu là ô chọn hình ảnh sản phẩm chính
